INSERT INTO `sl_xingyeqianyan` VALUES (262, '2019-08-27 07:23:11', '999', '温情入宝航，家访暖人心', 'http://192.168.0.106:8081/public/webuploader/upload/file_2019082713782.jpg', '380', '四川宝航人力资源管理有限公司优秀员工家访活动', '&lt;p style=&quot;text-indent:32px;text-align:center&quot;&gt;&lt;span style=&quot;;font-family:微软雅黑;font-size:16px&quot;&gt;温情入宝航，家访暖人心&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:32px;text-align:center&quot;&gt;&lt;span style=&quot;;font-family:微软雅黑;font-size:16px&quot;&gt;——四川宝航人力资源管理有限公司优秀员工家访活动&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:32px&quot;&gt;&lt;span style=&quot;;font-family:微软雅黑;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:微软雅黑&quot;&gt;构建和谐企业，传达人文关怀。&lt;/span&gt;2019年8月17日，&lt;a href=&quot;http://192.168.0.106:8081/&quot; target=&quot;_blank&quot;&gt;四川宝航人力资源管理有限公司&lt;/a&gt;&lt;/span&gt;&lt;span style=&quot;;font-family:微软雅黑;font-size:16px&quot;&gt;黄总&lt;/span&gt;&lt;span style=&quot;;font-family:微软雅黑;font-size:16px&quot;&gt;、范总带领着公司领导层走进了优秀员工陈瑶家中，对员工及其家属送上了关怀和慰问。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:32px&quot;&gt;&lt;span style=&quot;;font-family:微软雅黑;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:微软雅黑&quot;&gt;领导将&lt;/span&gt;“优秀员工”荣誉证书送至陈瑶及其父母手中，感谢他与宝航风雨同行，感谢家庭对员工的工作支持。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:32px&quot;&gt;&lt;span style=&quot;;font-family:微软雅黑;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:微软雅黑&quot;&gt;陈瑶自入职以来，表现优异，现任财务结算中心代结算主管，每年为公司兢兢业业，已经蝉联了三个月公司&lt;/span&gt;A分榜榜首。如此优异的表现必然也离不开家人的培养、支持与鼓励！&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/public/upload/ueditor/images/20190827/1566890504.jpg&quot; title=&quot;1566890504.jpg&quot; alt=&quot;21.jpg&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:32px&quot;&gt;&lt;span style=&quot;;font-family:微软雅黑;font-size:16px&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:32px&quot;&gt;&lt;span style=&quot;;font-family:微软雅黑;font-size:16px&quot;&gt;